import React, {createContext, useState} from 'react';

export const FilterContext = createContext();

const FilterContextProvider = (props) => {
	// get store information from accessed URL ex) https://fsai-dash.com/HQ returns HQ
	const storeFromUrl = window.location.href.split('/').slice(-1)[0]

	const [startDate, setStartDate] = useState(new Date('08/11/2021'));
	const [endDate, setEndDate] = useState(new Date('09/22/2021'));
	const [store, setStore] = useState(storeFromUrl);
	const [selectedStore, setSelectedStore] = useState(storeFromUrl);
	const [visible, setVisible] = useState('');
//	function setDisable() {
//		setVisible(true);
//	};
//	
//	function setEnable(){
//		setVisible(false);
//	}
									//setDisable: setDisable, 
									//setEnable: setEnable

	return (
		<FilterContext.Provider value={{ 
									start: [startDate, setStartDate], 
									end: [endDate, setEndDate], 
									store: [store, setStore], 
									selectedStore: [selectedStore, setSelectedStore], 
									visible: [visible, setVisible]
								  }}
		>
			{props.children}
		</FilterContext.Provider>
	);
}

export default FilterContextProvider;
