import Radio from './radio/Radio';
import { useState, useContext } from 'react';
import { FilterContext } from '../../../contexts/FilterContext';
import { useSelector } from 'react-redux';


function RadioButtons(){
	const { start, end, visible } = useContext(FilterContext);
	const [startDate, setStartDate] = start;
	const [endDate, setEndDate] = end;
	const [currentVisibleStatus] = visible;
	const current = new Date();
	const year = current.getFullYear();
	const month = current.getMonth();
	const day = current.getDate();
	const visibleStatus = useSelector(state => state.visible);


	function thisYear() {
		setStartDate(new Date('01/01/' + year));
		setEndDate(new Date('12/31/' + year));
	}

	function thisMonth() {
		setStartDate(new Date(year, month, 1));
		setEndDate(new Date(year, month + 1, 0));
	}

	function thisWeek() {
		let startday = day - current.getDay();
		let lastday = startday + 6;
		setStartDate(new Date(year, month, startday));
		setEndDate(new Date(year, month, lastday));
	}

	function yesterday() {
		setStartDate(new Date(year, month, day - 1));
		setEndDate(new Date(year, month, day - 1));
	}

	function lastYear() {
		setStartDate(new Date(year - 1, 0, 1));
        setEndDate(new Date(year - 1, 11, 31));
	}

	function lastMonth() {
		setStartDate(new Date(year, month - 1, 1));
        setEndDate(new Date(year, month, 0));
	}

	function lastWeek() {
		let startday = day - current.getDay() - 7;
        let lastday = startday + 6;
		setStartDate(new Date(year, month, startday));
        setEndDate(new Date(year, month, lastday));
	}

	function other() {
		console.log('other');
	}

//	return (
//		<section>
//			{year}
//			{month}
//			{day}
//			<ul>
//				<li><Radio name='timeScale' id='li1' text='This year' onChange={thisYear}/></li>
//				<li><Radio name='timeScale' id='li2' text='This month' onChange={thisMonth}/></li>
//				<li><Radio name='timeScale' id='li3' text='This week' onChange={thisWeek}/></li>
//				<li><Radio name='timeScale' id='li4' text='Yesterday' onChange={yesterday}/></li>
//				<li><Radio name='timeScale' id='li5' text='Last year' onChange={lastYear}/></li>
//				<li><Radio name='timeScale' id='li6' text='Last month' onChange={lastMonth}/></li>
//				<li><Radio name='timeScale' id='li7' text='Last week' onChange={lastWeek} /></li>
//				<li><Radio name='timeScale' id='li8' text='Other' onChange={other}/></li>
//			</ul>
//		</section>
//	);
	return (
		<section>
			<ul>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li1' onChange={thisYear} disabled={ visibleStatus } />
						<span>This year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li2' onChange={thisMonth} />
						<span>This month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li3' onChange={thisWeek} />
						<span>This week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li4' onChange={yesterday} />
						<span>Yesterday</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li5' onChange={lastWeek} defaultChecked />
						<span>Last week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li6' onChange={lastMonth} />
						<span>Last month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li7' onChange={lastYear} />
						<span>Last year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li8' onChange={other} />
						<span>Other</span>
					</label>
				</li>
			</ul>
		</section>
	);
}

export default RadioButtons;
