import { useContext } from 'react';
import { ContextFilter } from '../../../components/filter/ContextFilter';


function RadioButtons(){
	const { stateStart, stateEnd, stateVisible } = useContext(ContextFilter);
	const setStartDate = stateStart[1];
	const setEndDate = stateEnd[1];
	const currentVisibleStatus = stateVisible[0];
	const today = new Date();
	const thisyear = today.getFullYear();
	const thismonth = today.getMonth();
	const thisdate = today.getDate();

	function thisYear() {
		setStartDate(new Date('01/01/' + thisyear));
		setEndDate(today - 1);
	}

	function thisMonth() {
		setStartDate(new Date(thisyear, thismonth, 1));
		setEndDate(today - 1);
	}

	function thisWeek() {
		let thisweekStart = thisdate - today.getDay();
		setStartDate(new Date(thisyear, thismonth, thisweekStart));
		setEndDate(today - 1);
	}

	function yesterday() {
		setStartDate(today - 1);
		setEndDate(today - 1);
	}

	function lastYear() {
		setStartDate(new Date(thisyear - 1, 0, 1));
        setEndDate(new Date(thisyear - 1, 11, 31));
	}

	function lastMonth() {
		setStartDate(new Date(thisyear, thismonth - 1, 1));
        setEndDate(new Date(thisyear, thismonth, 0));
	}

	function lastWeek() {
		let lastweekStart = thisdate - today.getDay() - 7;
        let lastweekEnd = lastweekStart + 6;
		setStartDate(new Date(thisyear, thismonth, lastweekStart));
        setEndDate(new Date(thisyear, thismonth, lastweekEnd));
	}

	function other() {
		console.log('other');
	}

//	return (
//		<section>
//			{thisyear}
//			{thismonth}
//			{thisdate}
//			<ul>
//				<li><Radio name='timeScale' id='li1' text='This thisyear' onChange={thisYear}/></li>
//				<li><Radio name='timeScale' id='li2' text='This thismonth' onChange={thisMonth}/></li>
//				<li><Radio name='timeScale' id='li3' text='This week' onChange={thisWeek}/></li>
//				<li><Radio name='timeScale' id='li4' text='Yesterthisdate' onChange={yesterthisdate}/></li>
//				<li><Radio name='timeScale' id='li5' text='Last thisyear' onChange={lastYear}/></li>
//				<li><Radio name='timeScale' id='li6' text='Last thismonth' onChange={lastMonth}/></li>
//				<li><Radio name='timeScale' id='li7' text='Last week' onChange={lastWeek} /></li>
//				<li><Radio name='timeScale' id='li8' text='Other' onChange={other}/></li>
//			</ul>
//		</section>
//	);
	return (
		<section>
			<ul>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li1' onChange={thisYear} disabled={ currentVisibleStatus }  />
						<span>This year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li2' onChange={thisMonth} />
						<span>This month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li3' onChange={thisWeek} />
						<span>This week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li4' onChange={yesterday} />
						<span>Yesterday</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li5' onChange={lastWeek} defaultChecked />
						<span>Last week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li6' onChange={lastMonth} />
						<span>Last month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li7' onChange={lastYear} />
						<span>Last year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li8' onChange={other} />
						<span>Other</span>
					</label>
				</li>
			</ul>
		</section>
	);
}

export default RadioButtons;
