import { useState, useContext} from 'react';
import Filter from './components/filter/Filter';
import Dashboard from './components/dashboard/Dashboard';
import { FilterContext } from './contexts/FilterContext';
import { DashboardContext } from './contexts/DashboardContext';
import Axios from 'axios';
import getData from './api/app';

function App() {
	const { start, end, store, selectedStore } = useContext(FilterContext);
	const [startDate, setStartDate] = start;
	const [endDate, setEndDate] = end;
	const [defaultStore, setDefaultStore] = store;
	const [newSelectedStore, setNewSelectedStore] = selectedStore;

	const { data } = useContext(DashboardContext);
	const [newData, setNewData] = data;

	async function test() {
		let a = await getData();
		setNewData(a.data.results[0]);
	};


	return (
		<div className="App">
			<Filter />
			<Dashboard />
			<button onClick={ test }>Filter</button>
			<p> Default(URL) Store: {defaultStore}</p>
			<p> Selected Store: {newSelectedStore}</p>
		</div>
	  );
}

export default App;
