import config from '../../../config.json';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import { useState, useContext } from 'react';
import { FilterContext } from '../../../contexts/FilterContext';


function SelectStoreDropdown(){

//	const options = Object.values(config.config_stores); 
	const { store } = useContext(FilterContext);
	const [defaultStore, setStore] = store;
	const defaultOption = defaultStore;
	const options = [];
	if (defaultStore === 'HQ'){
		Object.entries(config.config_stores).map(([key, value]) => {
			options.push({value: key, label: value});
		});
	}
	else{
		Object.entries(config.config_stores).map(([key, value]) => {
			if (key.includes(defaultStore)){
				options.push({value: key, label: value});	
			}
		});
	}

	const handleSelect = (e) => {
		console.log(e);
	}

	return (
		<div>
			<Dropdown options={options} value={defaultOption} onChange={handleSelect} />

		</div>
	);
}

export default SelectStoreDropdown;
