import { useContext} from 'react';
import { ContextDashboard } from '../ContextDashboard';
import styles from './StylePromoDiscountGraph.module.css';
import CanvasJSReact from "../../../lib/canvasjs.react";
let CanvasJS = CanvasJSReact.CanvasJS;
let CanvasJSChart = CanvasJSReact.CanvasJSChart;

function PromoDiscountGraph() {
	const { data } = useContext(ContextDashboard);
	const newData = data[0];

	const options = {
      title: {
        text: "Basic Column Chart in React"
      },
      data: [{
                type: "column",
                dataPoints: [
                    { label: "Apple",  y: 10  },
                    { label: "Orange", y: 15  },
                    { label: "Banana", y: 25  },
                    { label: "Mango",  y: 30  },
                    { label: "Grape",  y: 28  }
                ]
       }]
   }

	return (
		<div>
			<table>
				<tbody>
					<tr>
						<td className={styles.category}>Total Sales</td>
						<td className={styles.category}>Food Sales</td>
						<td className={styles.category}>Bar Sales</td>
					</tr>
					<tr>
						<td className={styles.detail}>total_sales_amount</td>
						<td className={styles.detail}>food_sales_amount</td>
						<td className={styles.detail}>bar_sales_amount</td>
					</tr>
				</tbody>
			</table>
		</div>
	);
}

export default PromoDiscountGraph;
