import Radio from './radio/Radio';
import { useState, useContext } from 'react';
import { FilterContext } from '../../../contexts/FilterContext';


function RadioButtons(){
	const { start, end} = useContext(FilterContext);
	const [startDate, setStartDate] = start;
	const [endDate, setEndDate] = end;
	const current = new Date();
	const year = current.getFullYear();
	const month = current.getMonth() + 1;
	const day = current.getDate();

	function thisYear() {
		setStartDate(new Date(year, '01', '01'));
		setEndDate(new Date(year, '12', 31));
	}

	function thisMonth() {
		setStartDate(new Date(year, month - 1, 1));
		setEndDate(new Date(year, month, 0));
	}

	function thisWeek() {
		console.log('this week');
	}

	function today() {
		console.log('today');
	}

	function yesterday() {
		console.log('yesterday');
	}

	function lastYear() {
		console.log('last year');
	}

	function lastMonth() {
		console.log('last month');
	}

	function lastWeek() {
		console.log('last week');
	}

	function other() {
		console.log('other');
	}

//	return (
//		<section>
//			{year}
//			{month}
//			{day}
//			<ul>
//				<li><Radio name='timeScale' id='li1' text='This year' onChange={thisYear}/></li>
//				<li><Radio name='timeScale' id='li2' text='This month' onChange={thisMonth}/></li>
//				<li><Radio name='timeScale' id='li3' text='This week' onChange={thisWeek}/></li>
//				<li><Radio name='timeScale' id='li4' text='Yesterday' onChange={yesterday}/></li>
//				<li><Radio name='timeScale' id='li5' text='Last year' onChange={lastYear}/></li>
//				<li><Radio name='timeScale' id='li6' text='Last month' onChange={lastMonth}/></li>
//				<li><Radio name='timeScale' id='li7' text='Last week' onChange={lastWeek} /></li>
//				<li><Radio name='timeScale' id='li8' text='Other' onChange={other}/></li>
//			</ul>
//		</section>
//	);
	return (
		<section>
			{year}
			{month}
			{day}
			<ul>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li1' onChange={thisYear} />
						<span>This year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li2' onChange={thisMonth} />
						<span>This month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li3' onChange={thisWeek} />
						<span>This week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li4' onChange={today} />
						<span>Today</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li5' onChange={yesterday} />
						<span>Yesterday</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li6' onChange={lastWeek} defaultChecked />
						<span>Last week</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li7' onChange={lastMonth} />
						<span>Last month</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li8' onChange={lastYear} />
						<span>Last year</span>
					</label>
				</li>
				<li>
					<label>
						<input type='radio' name='timeScale' id='li9' onChange={other} />
						<span>Other</span>
					</label>
				</li>
			</ul>
		</section>
	);
}

export default RadioButtons;
